  const { articles, getArticles } = useStore();

  useEffect(() => {
    getArticles();
    console.log(articles);
  }, []);

  return (
    <div>
      {articles && <ArticleListPage article={articles} />}
    </div>
  );
};

export default ArticleContainer;
